/**
 * 
 */
import React, { useState } from 'react';
import { Grid, Typography, Paper, Box } from '@mui/material';
import FormInput from '../components/FormInput';
import ResultsDisplay from '../components/ResultsDisplay';
import DownloadButton from '../components/DownloadButton';
import { calculateTrench } from '../services/api';

const HomePage = () => {
  const [results, setResults] = useState(null);
  const [formData, setFormData] = useState(null);

  const handleFormSubmit = async (data) => {
    try {
      const response = await calculateTrench(data);
      setResults(response);
      setFormData(data); // Store form data for download functionality
    } catch (error) {
      console.error('Error calculating trench:', error);
      setResults({ violations: ['Error processing request'], finalTrenchWidth: 0, finalTrenchDepth: 0 });
    }
  };

  return (
    <Box sx={{ padding: 4 }}>
      <Paper elevation={3} sx={{ padding: 3, marginBottom: 3 }}>
        <Typography variant="h4" gutterBottom>
          Trench Calculation Input
        </Typography>
        <FormInput onSubmit={handleFormSubmit} />
      </Paper>
      {results && (
        <Paper elevation={3} sx={{ padding: 3, marginBottom: 3 }}>
          <Typography variant="h5" gutterBottom>
            Results
          </Typography>
          <ResultsDisplay results={results} />
          <Grid container justifyContent="flex-end" sx={{ marginTop: 2 }}>
            <DownloadButton trenchType={formData?.trenchType} conduits={formData?.conduits} />
          </Grid>
        </Paper>
      )}
    </Box>
  );
};

export default HomePage;
