import React, { useState } from 'react';
import {
  Grid,
  TextField,
  Button,
  Checkbox,
  Typography,
} from '@mui/material';

const FormInput = ({ onSubmit }) => {
  const [trenchType, setTrenchType] = useState('Distribution');
  const [conduits, setConduits] = useState([]);

  const handleAddConduit = () => {
    setConduits([...conduits, { type: '', sizes: '', count: '', checked: false }]);
  };

  const handleRemoveConduits = () => {
    const updatedConduits = conduits.filter((conduit) => !conduit.checked);
    setConduits(updatedConduits);
  };

  const handleCheckboxChange = (index) => {
    const updatedConduits = [...conduits];
    updatedConduits[index].checked = !updatedConduits[index].checked;
    setConduits(updatedConduits);
  };

  const handleInputChange = (index, field, value) => {
    const updatedConduits = [...conduits];
    updatedConduits[index][field] = value;
    setConduits(updatedConduits);
  };

  const handleSubmit = () => {
    onSubmit({ trenchType, conduits });
  };

  return (
    <Grid container spacing={3} style={{ padding: '20px' }}>
      <Grid item xs={12}>
        <Typography variant="h5">Trench Calculation Input</Typography>
      </Grid>
      {conduits.map((conduit, index) => (
        <Grid container spacing={2} key={index} alignItems="center" style={{ marginTop: '10px' }}>
          <Grid item xs={1}>
            <Checkbox
              checked={conduit.checked}
              onChange={() => handleCheckboxChange(index)}
            />
          </Grid>
          <Grid item xs={3}>
            <TextField
              label="Conduit Type"
              value={conduit.type}
              onChange={(e) =>
                handleInputChange(index, 'type', e.target.value)
              }
              fullWidth
            />
          </Grid>
          <Grid item xs={3}>
            <TextField
              label="Sizes (comma-separated)"
              value={conduit.sizes}
              onChange={(e) =>
                handleInputChange(index, 'sizes', e.target.value)
              }
              fullWidth
            />
          </Grid>
          <Grid item xs={3}>
            <TextField
              label="Count"
              type="number"
              value={conduit.count}
              onChange={(e) =>
                handleInputChange(index, 'count', e.target.value)
              }
              fullWidth
            />
          </Grid>
        </Grid>
      ))}
      <Grid container spacing={2} style={{ marginTop: '20px' }}>
        <Grid item xs={6}>
          <Button
            variant="contained"
            color="primary"
            fullWidth
            onClick={handleAddConduit}
          >
            Add Conduit
          </Button>
        </Grid>
        {conduits.length > 0 && (
          <Grid item xs={6}>
            <Button
              variant="contained"
              color="secondary"
              fullWidth
              onClick={handleRemoveConduits}
            >
              Remove Selected
            </Button>
          </Grid>
        )}
      </Grid>
      <Grid item xs={12} style={{ marginTop: '20px' }}>
        <Button
          variant="contained"
          color="secondary"
          style={{ width: '100%' }}
          onClick={handleSubmit}
        >
          Calculate
        </Button>
      </Grid>
    </Grid>
  );
};

export default FormInput;
