import React, { useState } from 'react';
import {
  Grid,
  TextField,
  Button,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  Typography,
} from '@mui/material';

const FormInput = ({ onSubmit }) => {
  const [trenchType, setTrenchType] = useState('Distribution');
  const [conduits, setConduits] = useState([]);

  const handleAddConduit = () => {
    setConduits([...conduits, { type: '', sizes: '', count: '' }]);
  };

  const handleRemoveConduit = (index) => {
    const updatedConduits = conduits.filter((_, i) => i !== index);
    setConduits(updatedConduits);
  };

  const handleInputChange = (index, field, value) => {
    const updatedConduits = [...conduits];
    updatedConduits[index][field] = value;
    setConduits(updatedConduits);
  };

  const handleSubmit = () => {
    onSubmit({ trenchType, conduits });
  };

  return (
    <Grid container spacing={3} style={{ padding: '20px' }}>
      <Grid item xs={12}>
       {/*} <Typography variant="h5">Trench Calculation Input</Typography>*/}
      </Grid>
      <Grid item xs={12}>
        <FormControl fullWidth>
          <InputLabel>Trench Type</InputLabel>
          <Select
            value={trenchType}
            onChange={(e) => setTrenchType(e.target.value)}
          >
            <MenuItem value="Distribution">Distribution</MenuItem>
            <MenuItem value="Service">Service</MenuItem>
          </Select>
        </FormControl>
      </Grid>
      {conduits.map((conduit, index) => (
        <Grid container spacing={2} key={index} style={{ marginTop: '10px'}}>
          <Grid item xs={3}>
            <TextField
              label="Conduit Type"
              value={conduit.type}
              onChange={(e) =>
                handleInputChange(index, 'type', e.target.value)
              }
              fullWidth
            />
          </Grid>
          <Grid item xs={3}>
            <TextField
              label="Sizes (comma-separated)"
              value={conduit.sizes}
              onChange={(e) =>
                handleInputChange(index, 'sizes', e.target.value)
              }
              fullWidth
            />
          </Grid>
          <Grid item xs={3}>
            <TextField
              label="Count"
              type="number"
              value={conduit.count}
              onChange={(e) =>
                handleInputChange(index, 'count', e.target.value)
              }
              fullWidth
            />
          </Grid>
          <Grid item xs={3}>
            <Button
              variant="outlined"
              color="secondary"
              onClick={() => handleRemoveConduit(index)}
            >
              Remove
            </Button>
          </Grid>
        </Grid>
      ))}
      <Grid item xs={12}>
        <Button variant="contained" color="primary" onClick={handleAddConduit}>
          Add Conduit
        </Button>
      </Grid>
      <Grid item xs={12}>
        <Button
          variant="contained"
          color="secondary"
          style={{ marginTop: '10px' }}
          onClick={handleSubmit}
        >
          Calculate
        </Button>
      </Grid>
    </Grid>
  );
};

export default FormInput;
